mapper.update:
UPDATE file_info
file_md5 = #{fileMd5}, file_pid = #{filePid}, file_size = #{fileSize}, file_name = #{fileName}, file_cover = #{fileCover}, file_path = #{filePath}, create_time = #{createTime}, last_update_time = #{lastUpdateTime}, folder_type = #{folderType}, file_category = #{fileCategory}, file_type = #{fileType}, status = #{status}, recovery_time = #{recoveryTime}, del_flag = #{delFlag}
WHERE user_id = #{userId} and file_id = #{fileId}

mapper.select:
select
file_id, file_pid, file_size, file_name, file_cover, create_time, last_update_time,
folder_type, file_category, file_type, status
from file_info
where user_id = #{userId}
 and file_pid=#{filePid}
 and file_category = #{category}
and del_flag = 2
order by last_update_time desc

select * from file_info
where user_id = #{userId} and file_pid = #{filePid} and file_name = #{fileName}
and folder_type=#{folderType}
and del_flag = 2

select * from file_info
where  file_id = #{fileId} and user_id = #{userId}
 and folder_type=#{folderType}
and del_flag = 2

select file_id, file_pid, file_size, file_name, file_cover, create_time, last_update_time, folder_type, file_category, file_type, status from file_info where file_pid = #{filePid} and user_id = #{userId}
and file_id not in
#{fileId}
and folder_type=1 and del_flag = 2 order by create_time desc
